svg.script:
const quotes = [
  "I will embrace every pleasure and every pain this world has to offer. — Ainz Ooal Gown",
  "Weakness disgusts me. Rise or be forgotten. — Demiurge",
  "If you win, you live. If you lose, you die. If you don’t fight, you can’t win! — Eren Yeager",
  "Devote your hearts! — Erwin Smith",
  "This world is cruel, but it is also very beautiful. — Mikasa",
  "Scars on the back are a swordsman’s shame. — Zoro",
  "Inherited Will cannot be stopped. — Gol D. Roger",
  "Go forward with your heart ablaze. — Rengoku",
  "Sometimes you have to hurt someone to stop them from hurting themselves. — Kaneki",
  "If you're always worried about crushing the ants beneath you... you won’t be able to walk. — Guts",
  "I am Justice! — Light Yagami",
  "People's lives end when they lose faith. — Naruto",
  "Those who abandon their friends are worse than scum. — Kakashi",
  "Wake up to reality. Nothing ever goes as planned. — Madara",
  "Be water, my friend. — Bruce Lee",
  "He who has a why can bear any how. — Nietzsche",
  "The ones crazy enough to think they can change the world are the ones who do. — Steve Jobs"
];

function updateQuote() {
  const quoteElement = document.getElementById("quote");
  const random = quotes[Math.floor(Math.random() * quotes.length)];
  quoteElement.textContent = random;
}

setInterval(updateQuote, 5000);
updateQuote();
